-- Table structure for 'user' table
CREATE TABLE `user` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `firstname` VARCHAR(255) NOT NULL,
    `lastname` VARCHAR(255) NOT NULL,
    `username` VARCHAR(255) NOT NULL UNIQUE,
    `password` VARCHAR(255) NOT NULL,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

-- Table Structure for 'background'
CREATE TABLE `background` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `name` VARCHAR(255) DEFAULT NULL,
    `file` blob DEFAULT NULL,
    `userId` int(11) NOT NULL,
    PRIMARY KEY (`id`),
    FOREIGN KEY (`userId`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


-- Table for 'part' types
CREATE TABLE `part_types` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `type_name` VARCHAR(16)  NOT NULL,
    PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

-- Table structure for 'parts'
-- CREATE DOMAIN degrees numeric (6,2) CHECK (VALUE >= 0 AND VALUE <= 360) -- not mysql syntax!!
CREATE TABLE `parts` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL DEFAULT '',
    `type` int(11) NOT NULL DEFAULT '5',     -- VARCHAR(255) NOT NULL DEFAULT '',
    `file` blob DEFAULT NULL,
    `userId` int(11) NOT NULL,
    PRIMARY KEY (`id`),
    FOREIGN KEY (`type`) REFERENCES `part_types` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (`userId`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

-- Table structure for 'monster' table
CREATE TABLE `monster` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `name` VARCHAR(255) NOT NULL,
    `userId` int(11) NOT NULL DEFAULT '0',
    `backgroundId` int(11) NOT NULL DEFAULT '1',
    `file` blob DEFAULT NULL,
    `size` int(11) NOT NULL DEFAULT 100,
    `xCoord` int(11) NOT NULL DEFAULT 0,
    `yCoord` int(11) NOT NULL DEFAULT 0,
    PRIMARY KEY (`id`),
    KEY `userId` (`userId`),
    FOREIGN KEY (`userId`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
    KEY `backgroundId` (`backgroundId`),
    FOREIGN KEY (`backgroundId`) REFERENCES `background` (`id`) ON DELETE RESTRICT ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=latin1;



CREATE TABLE `monster_parts` (
    `id` int(11) NOT NULL AUTO_INCREMENT,
    `id_monster` int(11) NOT NULL DEFAULT '0',
    `id_parts` int(11) NOT NULL DEFAULT '0',
    `size` int(11) NOT NULL DEFAULT 100,
    `orientation` int(11) DEFAULT '0',
    `zIndex` int(11) NOT NULL DEFAULT '1',	
    `xCoord` int(11) NOT NULL DEFAULT 0,
    `yCoord` int(11) NOT NULL DEFAULT 0,
    PRIMARY KEY (`id`),
    KEY `id_monster` (`id_monster`),
    CONSTRAINT `monsterIdRefFK` FOREIGN KEY (`id_monster`) REFERENCES `monster` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
    KEY `id_parts` (`id_parts`),
    CONSTRAINT `partsIdRefFK` FOREIGN KEY (`id_parts`) REFERENCES `parts` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
    CONSTRAINT 360degrees CHECK (`orientation` >= 0 AND `orientation` <= 360),
    CONSTRAINT 5To100 CHECK (`size` >= 5 AND `size` <= 100) 
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

